const s = $json.sistolica;
const d = $json.diastolica;
let nivel = "Normal";

if (s >= 180 || d >= 120) nivel = "Crisis Hipertensiva 🚨";
else if (s >= 160 || d >= 100) nivel = "Hipertensión Etapa 2";
else if (s >= 140 || d >= 90) nivel = "Hipertensión Etapa 1";
else if (s >= 130 || d >= 85) nivel = "Elevada";

// Obtener el correo desde el nodo anterior (Get row(s) in sheet1)
const rows = $input.first().json.rows || [];
const doctorEmail = rows[0]?.correo || $('Webhook').first().json.body.doctorEmail; // valor por defecto

const texto = `
🩺 *Reporte TensiGuard - ${$json.nombre}*
---------------------------------
📅 Fecha: ${$json.fecha}
🧍‍♂️ Género: ${$json.genero}
⚖️ Peso: ${$json.peso} kg
📏 Altura: ${$json.altura} cm

💓 Presión actual: *${s}/${d} mmHg*
📊 Clasificación: *${nivel}*
📖 Circunstancia: ${$json.circunstancia}

🧠 Análisis IA:
${$json.reporte_ai}

⚠️ Médico asignado: ${$json.medico_asignado || "No especificado"}
`;

return [{
  json: {
    texto,
    nivel,
    nombre: $json.nombre,
    sistolica: s,
    diastolica: d,
    fecha: $json.fecha,
    medico_asignado: $json.medico_asignado,
    telegram_chat_id: $json.telegram_chat_id || "5321589234",
    doctorEmail // ahora sí se enviará correctamente
  }
}];

// Obtenemos los datos del nodo anterior (Google Sheets)
const rows = items[0].json.rows || [];

// Si no hay filas, devolvemos un mensaje indicando que no hay historial
if (rows.length === 0) {
  return [{
    json: {
      mensaje: "No se encontraron registros previos para este paciente."
    }
  }];
}

// Convertimos las filas a objetos limpios
const historial = rows.map(row => ({
  id_lectura: row.id_paciente || row.nombre,
  nombre: row.nombre,
  peso: row.peso || "N/A",
  altura: row.altura || "N/A",
  genero: row.genero || "N/A",
  sistolica: parseInt(row.sistolica) || 0,
  diastolica: parseInt(row.diastolica) || 0,
  clasificacion: row.clasificacion || "No definida",
  fecha: row.fecha
}));

// Devolvemos cada registro como un item
return historial.map(item => ({ json: item }));

